import React, { useState } from "react";

export default function TextForm(props) {
  const [text, setText] = useState("Enter Text Here");
  const UpCase = () => {
    // console.log("Text has been converted into upper case");
    const newText = text.toUpperCase();
    setText(newText);
  };
  const handleOnChange = (event) => {
    // console.log("handling onchange");
    setText(event.target.value); //this way we are constantly udating the text variable as soon as the use is enter text in the textBox
  };
  const showPreview = () => {
    document.getElementById("preview").classList.toggle();
  };
  return (
    <>
      <div className="container my-5">
        <h1>{props.heading}</h1>
        <div className="mb-3 ">
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="textBox"
            rows="10"
          ></textarea>
        </div>
        <button type="button" onClick={UpCase} className="btn btn-primary">
          Upper Case
        </button>
      </div>
      <div className="container my-3">
        <h2>Your text summary</h2>
        <p>
          {text.split(" ").length} words and {text.length} characters
        </p>
        <p>{0.008 * 60 * text.split(" ").length} Second read</p>
        <h2>Preview</h2>
        <p id="preview">{text}</p>
        <button className="btn btn-primary" onClick={showPreview}>
          Show Preview
        </button>
      </div>
    </>
  );
}
